mapper.select:
SELECT basic_num,basic_name,basic_days,basic_fountain,basic_creation_people,basic_creation_time FROM basic

    /*如果线路名称不等于空的话,执行查询操作*/

        and basic_name=#{basic_name}

    /*如果线路编号不等于空的话,执行查询操作*/

        and basic_num=#{basic_num}

    /*如果行程天数不等于空的话,执行查询操作*/

        and basic_days=#{basic_days}
