mapper.select:
select hotel_id,hotel_name,hotel_abbreviation,hotel_type,hotel_class, hotel_fax,hotel_email,hotel_phone,hotel_province,hotel_city, hotel_county,hotel_address,hotel_introduce,hotel_facilities, hotel_notice,hotel_time,hotel_owner ,r.relation_id,r.relation_type_id,r.`relation_owner`, r.relation_name,r.relation_phone,r.relation_email,r.relation_type ,i.img_id,i.img_path,i.img_time,i.img_type,i.other_id,i.supplier_type from hotel left join relation r on r.relation_type_id = hotel.hotel_id and (r.relation_type = 2 or r.relation_type is null) left join image i on i.other_id = hotel.hotel_id and (i.supplier_type = 2 or i.supplier_type is null)
and hotel_province = #{hotel_province}
and hotel_city = #{hotel_city}
and hotel_county = #{hotel_county}
and (hotel_name like CONCAT('%',#{hotel_name},'%') or hotel_abbreviation like CONCAT('%',#{hotel_abbreviation},'%'))

mapper.update:
UPDATE hotel
hotel_type=#{hotel_type}, hotel_class=#{hotel_class}, hotel_fax=#{hotel_fax}, hotel_email=#{hotel_email}, hotel_phone=#{hotel_phone}, hotel_province=#{hotel_province}, hotel_city=#{hotel_city}, hotel_county=#{hotel_county}, hotel_address=#{hotel_address}, hotel_introduce=#{hotel_introduce}, hotel_facilities=#{hotel_facilities}, hotel_notice=#{hotel_notice}, hotel_owner=#{hotel_owner},
WHERE hotel_id=#{hotel_id}

UPDATE relation
relation_name=#{relation_name}, relation_phone=#{relation_phone}, relation_email=#{relation_email},
WHERE relation_type = #{relation_type} and relation_id = #{relation_id}

UPDATE hotel_price
hotel_choice=#{hotel_choice}, hotel_costed_pay=#{hotel_costed_pay}, hotel_listed_pay=#{hotel_listed_pay},
WHERE hotel_price_id = #{hotel_price_id} and hotel_id = #{hotel_id}
